mapper.select:
SELECT law_record.id, law_record.law_type, law_record.status, law_case_code, law_record_agency.law_case_source, (CASE law_record.law_type WHEN 1 THEN law_record_inquire_.ship_name ELSE law_record_inquire_safe.ship_name END) ship_name, (CASE law_record.law_type WHEN 1 THEN law_record_inquire_.investigate_name ELSE law_record_inquire_safe.investigate_name END) investigate_name, (CASE law_record.law_type WHEN 1 THEN law_record_inquire_.investigate_tel ELSE law_record_inquire_safe.investigate_tel END) investigate_tel, (CASE law_record.law_type WHEN 1 THEN law_record_decision.punish_person_type ELSE law_record_decision_safe.punish_person_type END) punish_person_type, law_record.create_date FROM law_record LEFT JOIN law_record_agency ON law_record.id=law_record_agency.id LEFT JOIN (SELECT law_record_inquire.record_id record_id, GROUP_CONCAT(law_record_inquire.ship_name) ship_name, GROUP_CONCAT(law_record_inquire.investigate_name) investigate_name, GROUP_CONCAT(law_record_inquire.investigate_tel) investigate_tel FROM law_record_inquire GROUP BY law_record_inquire.record_id) law_record_inquire_ ON law_record.id = law_record_inquire_.record_id LEFT JOIN law_record_inquire_safe ON law_record.id=law_record_inquire_safe.id LEFT JOIN law_record_decision ON law_record.id=law_record_decision.id LEFT JOIN law_record_decision_safe ON law_record.id=law_record_decision_safe.id
law_record.status < 90
AND law_record.law_type =#{lawType}
AND like CONCAT("%",#{lawCaseCode},"%")
AND ( law_record_inquire_.ship_name like CONCAT("%",#{shipName},"%") or law_record_inquire_safe.ship_name like CONCAT("%",#{shipName},"%") )
AND (law_record_inquire_.investigate_tel=#{investigateTel} or law_record_inquire_safe.investigate_tel=#{investigateTel})
AND ( law_record_inquire_.investigate_name like CONCAT("%",#{investigateName},"%") or law_record_inquire_safe.investigate_name like CONCAT("%",#{investigateName},"%") )
AND law_record.status =#{status}
AND law_record.create_date > #{createStartTime}
AND law_record.create_date < #{createEndTime}
order by law_record.create_date desc

SELECT law_record.id id, law_record.law_type, law_record.status, law_case_code, law_record_agency.law_case_source, @rownum:=#{investigateName} AS investigateName_, -- (CASE law_record.law_type WHEN 1 THEN law_record_inquire.ship_name ELSE law_record_inquire_safe.ship_name END) ship_name, -- (CASE law_record.law_type WHEN 1 THEN law_record_inquire.id ELSE law_record_inquire_safe.id END) inquire_id, -- (CASE law_record.law_type WHEN 1 THEN law_record_inquire.investigate_name ELSE law_record_inquire_safe.investigate_name END) investigate_name, -- (CASE law_record.law_type WHEN 1 THEN law_record_inquire.investigate_tel ELSE law_record_inquire_safe.investigate_tel END) investigate_tel, (CASE law_record.law_type WHEN 1 THEN law_record_decision.punish_person_type ELSE law_record_decision_safe.punish_person_type END) punish_person_type, law_record.create_date FROM law_record LEFT JOIN law_record_agency ON law_record.id=law_record_agency.id -- LEFT JOIN law_record_inquire ON law_record.id=law_record_inquire.record_id -- LEFT JOIN law_record_inquire_safe ON law_record.id=law_record_inquire_safe.id LEFT JOIN law_record_decision ON law_record.id=law_record_decision.id LEFT JOIN law_record_decision_safe ON law_record.id=law_record_decision_safe.id
law_record.status < 90
AND law_record.law_type =#{lawType}
AND like CONCAT("%",#{lawCaseCode},"%")
AND law_record.status =#{status}
AND law_record.create_date > #{createStartTime}
AND law_record.create_date < #{createEndTime}
order by law_record.create_date desc

select id, ship_name, investigate_name, investigate_tel from law_record_inquire
law_record_inquire.record_id=#{id}
AND law_record_inquire.investigate_name like CONCAT("%",#{investigateName},"%")
