mapper.select:
select p.*,r.name as personName,r.birthday,r.sex,r.email,r.phone,r.position_id as positionId,s.name as lawShipName from ship_person p LEFT JOIN sys_user u ON p.person_id = u.id LEFT JOIN sys_dept d ON u.dept_id = d.id LEFT JOIN sys_user r ON p.person_id = r.id LEFT JOIN ship_info_ship s ON p.ship_id = s.id where p.delete_flag = 1
and r.name like CONCAT('%',#{person.personName},'%')
and p.id=#{person.id}
order by p.create_date desc LIMIT #{total} , #{size}

select p.* from ship_person p LEFT JOIN sys_user u ON p.person_id = u.id LEFT JOIN sys_dept d ON u.dept_id = d.id LEFT JOIN sys_user r ON p.person_id = r.id LEFT JOIN ship_info_ship s ON p.ship_id = s.id where p.delete_flag = 1
and r.name like CONCAT('%',#{person.personName},'%')
and p.id=#{person.id}
